create table app_user_profile
(
    user_id               varchar(20)                              not null comment '用户ID'
        primary key,
    base_info             json                                     not null comment '基础信息',
    ext_info              json                                     null comment '扩展信息',
    mv_score              decimal(5, 2)                            null comment 'MV总分',
    mv_detail             json                                     null comment 'MV各维度得分详情',
    service_matchmaker_id varchar(20)                              null comment '服务红娘ID',
    created_at            datetime(6) default CURRENT_TIMESTAMP(6) not null comment '创建时间',
    updated_at            datetime(6) default CURRENT_TIMESTAMP(6) not null on update CURRENT_TIMESTAMP(6) comment '更新时间',
    constraint FK_dda5a718945f3f43edca49970a8
        foreign key (user_id) references app_user (id)
);

