create table match_detail
(
    id                  bigint auto_increment
        primary key,
    batch_id            bigint        not null,
    candidate_id        varchar(20)   not null,
    mv_diff             decimal(5, 2) not null,
    is_mv_pass          tinyint       not null comment '1:通过, 0:不通过',
    is_personality_pass tinyint       not null comment '1:通过, 0:不通过',
    result_status       tinyint       not null comment '1:通过, 0:不通过',
    match_data          json          null comment '匹配详情数据快照',
    constraint FK_af158e72b27b4013ba7744f798f
        foreign key (batch_id) references match_batch (id),
    constraint FK_dd634bdc0560993c1c1af70a6d1
        foreign key (candidate_id) references app_user (id)
);

